import{_ as e,c as t,o,a4 as r}from"./chunks/framework.CmhZ5-CP.js";const _=JSON.parse('{"title":"SoundFont3 Structure","description":"","frontmatter":{"prev":{"text":"2.3. Preset data","link":"/routes/2.%20SF%202.04%20Spec/2.3.%20Preset%20data.html"},"next":{"text":"4. Development","link":"/routes/4.%20Development/README.html"}},"headers":[],"relativePath":"routes/3. SF 3 Spec/README.md","filePath":"routes/3. SF 3 Spec/README.md","lastUpdated":1719225593000}'),s={name:"routes/3. SF 3 Spec/README.md"},n=r('<h1 id="soundfont3-structure" tabindex="-1">SoundFont3 Structure <a class="header-anchor" href="#soundfont3-structure" aria-label="Permalink to &quot;SoundFont3 Structure&quot;">â€‹</a></h1><div class="tip custom-block"><p class="custom-block-title">Purpose</p><p>To understand SoundFont3 through <a href="https://github.com/musescore/sftools" target="_blank" rel="noreferrer">MuseScore/sftools</a> (unmaintained).</p><p>This section requires a <a href="/routes/2. SF 2.04 Spec/README.html">basic understanding of SoundFont 2.04</a>.</p></div><p>SoundFont3 was originally created by MuseScore. WAV samples are replaced with OGG VORBIS to achieve up to 10x lossy compression.</p><p>Unfortunately their implementation is non-compliant with SoundFont2:</p><ul><li><code>phdr</code> chunk does not end with <code>EOP</code></li><li><code>inst</code> chunk does not end with <code>EOI</code></li><li><code>shdr</code> chunk does not end with <code>EOS</code></li><li><code>shdr.startLoop</code> and <code>shdr.endLoop</code> are clearly out of range of <code>shdr.start</code> and <code>shdr.end</code></li></ul><p><strong>A fork is created to fix these issues: <a href="https://github.com/musidi-org/sftools" target="_blank" rel="noreferrer">sf3tools</a>.</strong></p>',6),a=[n];function d(c,i,u,l,p,h){return o(),t("div",null,a)}const m=e(s,[["render",d]]);export{_ as __pageData,m as default};
